package net.datafaker.idnumbers;

import net.datafaker.providers.base.BaseProviders;
import net.datafaker.providers.base.IdNumber;
import net.datafaker.providers.base.PersonIdNumber;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.mockito.Mockito;

import java.time.LocalDate;
import java.time.format.DateTimeFormatter;

import static net.datafaker.idnumbers.Utils.gender;
import static net.datafaker.idnumbers.Utils.multiply;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.Mockito.when;

public class UkrainianIdNumber_generateValidTest {

    private UkrainianIdNumber ukrainianIdNumber;
    private BaseProviders faker;
    private IdNumber.IdNumberRequest request;

    @BeforeEach
    public void setUp() {
        ukrainianIdNumber = new UkrainianIdNumber();
        faker = Mockito.mock(BaseProviders.class);
        request = Mockito.mock(IdNumber.IdNumberRequest.class);
    }

    @Test
    public void testGenerateValid() {
        LocalDate birthday = LocalDate.of(1990, 1, 1);
        String dob = DateTimeFormatter.ofPattern("yyyyMMdd").format(birthday);
        String numbers = "1234";
        int checksum = multiply(dob + numbers, UkrainianIdNumber.CHECKSUM_WEIGHTS) % 10;
        String expectedUnzr = dob + "-" + numbers + checksum;

        when(faker.timeAndDate().birthday()).thenReturn(birthday);
        when(faker.numerify("####")).thenReturn(numbers);
        when(gender(faker, request)).thenReturn("Male");

        PersonIdNumber result = ukrainianIdNumber.generateValid(faker, request);

        assertEquals(expectedUnzr, result.getIdNumber());
        assertEquals(birthday, result.getBirthday());
        assertEquals("Male", result.getGender());
    }

    @Test
    public void testGenerateInvalid() {
        LocalDate birthday = LocalDate.of(1990, 1, 1);
        String dob = DateTimeFormatter.ofPattern("yyyyMMdd").format(birthday);
        String numbers = "1234";
        int multiplied = multiply(dob + numbers, UkrainianIdNumber.CHECKSUM_WEIGHTS);
        int checksum = (multiplied + 1) % 10;
        String expectedUnzr = dob + "-" + numbers + checksum;

        when(faker.timeAndDate().birthday()).thenReturn(birthday);
        when(faker.numerify("####")).thenReturn(numbers);

        String result = ukrainianIdNumber.generateInvalid(faker);

        assertEquals(expectedUnzr, result);
    }
}
