package org.apache.commons.collections4;

import org.apache.commons.collections4.comparators.NullComparator;
import org.junit.jupiter.api.Test;

import java.util.Comparator;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

public class ComparatorUtils_nullHighComparatorTest {

    @Test
    public void testNullHighComparatorWithNullInput() {
        // Given
        Comparator<String> inputComparator = null;

        // When
        Comparator<String> result = ComparatorUtils.nullHighComparator(inputComparator);

        // Then
        assertTrue(result instanceof NullComparator);
        assertEquals(ComparatorUtils.NATURAL_COMPARATOR, ((NullComparator<String>) result).getComparator());
    }

    @Test
    public void testNullHighComparatorWithNonNullInput() {
        // Given
        Comparator<String> inputComparator = Comparator.naturalOrder();

        // When
        Comparator<String> result = ComparatorUtils.nullHighComparator(inputComparator);

        // Then
        assertTrue(result instanceof NullComparator);
        assertEquals(inputComparator, ((NullComparator<String>) result).getComparator());
    }
}
